    # Decryption
    img = Image.open("graph_based_hashing.png").convert("RGBA")
    width, height = img.size
    pixels = img.load()
    visited = set()
    for coordinate in start_points:
        visited.add(coordinate)
    path = []
    final_message=""
    i=0
    while True:
        start_x, start_y = start_points[i]
        message_extraction = ""
        message_extraction = dfs_decryption(start_x, start_y,False, pixels, visited, path, message_extraction)
        donne , check = binary_to_message(message_extraction)
        final_message += donne
        if check :
            break
        i+=1
    print(message == final_message)
    print(len(message))
    # print(final_message)

    canvas = Image.new("RGB", (width, height), "white")
